import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { ViewCard, CardWrapper, Button } from "../components";
import { submitOrder } from "../services/stateWrites";
import { addresses } from "@gelato-krystal/contracts";
const { DAI, USDC } = addresses;

const SubmitTask = ({ userAccount }) => {
  const [loading, setLoading] = useState(false);
  const [inputs, setInputs] = useState({});
  const [totalAmount, setTotalAmount] = useState(30);
  const [intervalSeconds, setIntervalSeconds] = useState(120);
  const [tradeNum, setTradeNum] = useState(3);

  // const inputsUpdates = async () => {
  //   const userProxy = await getUserProxy(userAccount);
  //   const vA = await getVault(userAccount, userProxy, "ETH-A");
  //   const vAId = vA !== undefined ? vA.id : 0;
  //   const vB = await getVault(userAccount, userProxy, "ETH-B");
  //   const vBId = vB !== undefined ? vB.id : 0;

  //   setInputs({
  //     ...inputs,
  //     vaultAId: vAId,
  //     vaultBId: vBId,
  //     defaultValueAmount: 30,
  //     defaultValueInterval: 120,
  //     defaultValueTradeNum: 3,
  //   });
  // };

  const handleTotalAmountChange = async (event) => {
    const newValue = event.target.value;
    setTotalAmount(newValue);
  };

  const handleIntervalSecondsChange = async (event) => {
    const newValue = event.target.value;
    setIntervalSeconds(newValue);
  };

  const handleTradeNumChange = async (event) => {
    const newValue = event.target.value;
    setTradeNum(newValue);
  };

  const submit = async () => {
    if (totalAmount === 0) return;
    if (intervalSeconds === 0) return;
    if (parseInt(inputs.tradeNum) > 10) {
      console.log("Max Trade Number is 10");
      return;
    }

    await submitOrder(
      userAccount,
      DAI,
      USDC,
      intervalSeconds,
      totalAmount / tradeNum,
      tradeNum
    );
  };

  // useEffect(() => {
  //   inputsUpdates();
  // });

  return (
    <>
      <CardWrapper>
        <ViewCard>
          <label style={{ margin: "10px" }}>
            Total Amount of DAI to sell for USDC
          </label>

          <input
            style={{ maxWidth: "80%" }}
            type="number"
            value={totalAmount}
            onChange={handleTotalAmountChange}
            defaultValueAmount={inputs.defaultValueAmount}
          />
        </ViewCard>

        <ViewCard>
          <label style={{ margin: "10px" }}>
            Interval between each trade (in seconds)
          </label>

          <input
            style={{ maxWidth: "80%" }}
            type="number"
            value={intervalSeconds}
            onChange={handleIntervalSecondsChange}
            defaultValueAmount={inputs.defaultValueInterval}
          />
        </ViewCard>

        <ViewCard>
          <label style={{ margin: "10px" }}>
            Number of trades to split the total amount up
          </label>

          <input
            style={{ maxWidth: "80%" }}
            type="number"
            value={tradeNum}
            onChange={handleTradeNumChange}
            defaultValueAmount={inputs.defaultValueTradeNum}
          />
        </ViewCard>

        <ViewCard>
          {!loading && (
            <>
              <label style={{ margin: "10px" }}>
                {`Execute ${tradeNum} trades, each worth ${parseFloat(
                  totalAmount / tradeNum
                ).toFixed(3)} DAI`}
              </label>
              <Button
                onClick={async () => {
                  setLoading(true);
                  try {
                    await submit();
                    setLoading(false);
                  } catch {
                    setLoading(false);
                  }
                }}
              >
                {`Submit Task`}
              </Button>
            </>
          )}
          {loading && <p>waiting...</p>}
        </ViewCard>
      </CardWrapper>
    </>
  );
};

export default SubmitTask;
