import { ethers } from "ethers";
import { getGelatoKrystal } from "./stateReads";

export const submitOrder = async (
  user,
  inToken,
  outToken,
  delay,
  amountPerTrade,
  nTrades,
  minSlippage = 9001,
  maxSlippage = 9000,
  gasPriceCeil = 0
) => {
  console.log("1");
  const gelatoKrystal = await getGelatoKrystal(user);
  console.log("2");

  const options = {
    gasPrice: ethers.utils.parseUnits("50", "gwei"),
  };
  console.log("3");
  const submitTx = await gelatoKrystal.submitDCAKyber(
    {
      inToken: inToken,
      outToken: outToken,
      amountPerTrade: amountPerTrade,
      nTrades: nTrades,
      minSlippage: minSlippage,
      maxSlippage: maxSlippage,
      delay: delay,
      gasPriceCeil: gasPriceCeil,
    },
    options
  );
  console.log("4");
  await submitTx.wait();
};
